select ca.id from udo_triplegcargo as tlc left join udo_tripleg as tl on (tl.id=tlc.tripLeg_id) left join udo_cargo as ca on (ca.id=tlc.cargo_id) left join udo_truck as t on (t.id=tl.truck_id) where ca.customer_id=15876 and tl.truck_id IN (571,587,589,605,701,702,703,704,705,706,707,708,709,2341,2342,2343,2372,2373,2374,2375,2870,3135) and ca.businessUnit_id = 1 and tl.loadingArrivalETA > 2015-03-31 22:00:00;
