select cu.tradingName, CONCAT(p.first_name, " ", p.last_name) as account_manager, CONCAT(pc.first_name, ' ', pc.last_name) as created_by INTO OUTFILE '/tmp/customer_without_bu.csv' FIELDS TERMINATED BY ',' OPTIONALLY ENCLOSED BY '"' LINES TERMINATED BY '\n' from udo_customer as cu left join udo_customerbusinessunit_link as cbu on (cbu.customer_id=cu.id) left join udo_customeradmindetails as cad on (cad.customer_id=cu.id) left join udo_accountmanager as am on (am.id=cad.accountManager_id) left join person as p on (p.id=am.person_id) left join permissionuser as pu on (pu.id=cu.created_by) left join person as pc on (pc.id=pu.person_id) where cbu.businessUnit_id IS NULL and cu.primaryCustomer=1 and cu.active=1;

