select ca.id, tl.truck_id, t.fleetnum, cu.id as customer_id, cu.tradingName, ca.rate_id, fvc.id as fandv_id, FORMAT((fvc.fixedCost/100),2) as fixedCost, fvc.startDate, fvc.endDate, FORMAT((drv.value/100),2) as var_rate from udo_cargo as ca left join udo_triplegcargo as tlc on (tlc.cargo_id=ca.id) left join udo_tripleg as tl on (tl.id = tlc.tripLeg_id) left join udo_customer as cu on (cu.id=ca.customer_id) left join udo_truck as t on (t.id=tl.truck_id) left join udo_fandvcontract as fvc on (fvc.id=ca.fandVContract_id) left join daterangevalue as drv on (drv.objectInstanceId=fvc.variableCostRate_id) where tlc.cargo_id=717098 and drv.type = "Rate"\G
