select r.id as refuel_id, r.time_created, r.litres, r.odo, CONCAT(p.first_name, " ", p.last_name) as created_by, ron.orderNumber, t.id as truck_id, t.fleetnum from udo_refuel as r left join udo_refuelordernumber as ron on (ron.id=r.refuelOrderNumber_id) left join udo_truck as t on (t.id=r.truck_id) left join permissionuser as pu on (pu.id=r.created_by) left join person as p on (p.id=pu.person_id) where t.fleetnum = "342" order by r.id desc limit 5;
