select id from udo_refuel where refuelOrderNumber_id IN (select id from udo_refuelordernumber where orderNumber in (295085,295761,295765,296558,296560,295432,295772,296563,296572,295094,295443,295774,296579,295105,295446,295778,295785,296583,295380,296394,295081,295108,295390,295424,295438,295743,295781,296432,296469,296494,296527,296557,296568));

select rf.id as refuel_id, rfo.orderNumber as orderNumber from udo_refuelordernumber as rfo left join udo_refuel as rf on (rf.refuelOrderNumber_id=rfo.id) where rfo.orderNumber in ();

select rf.id as refuel_id, rfo.orderNumber as orderNumber, t.fleetnum, dr.nickname as driver, lo.name as point from udo_refuelordernumber as rfo left join udo_refuel as rf on (rf.refuelOrderNumber_id=rfo.id) left join udo_truck as t on (t.id=rf.truck_id) left join udo_driver as dr on (dr.id=rf.driver_id) left join udo_point as po on (po.id=rf.point_id) left join udo_location as lo on (lo.id=po._udo_Location_id) where rfo.orderNumber in (295085,295761,295765,296558,296560,295432,295772,296563,296572,295094,295443,295774,296579,295105,295446,295778,295785,296583,295380,296394,295081,295108,295390,295424,295438,295743,295781,296432,296469,296494,296527,296557,296568) and rf.id IS NOT NULL;

select rf.id as refuel_id, rfo.orderNumber as orderNumber, t.fleetnum, dr.nickname as driver, lo.name as point from udo_refuelordernumber as rfo left join udo_refuel as rf on (rf.refuelOrderNumber_id=rfo.id) left join udo_truck as t on (t.id=rf.truck_id) left join udo_driver as dr on (dr.id=rf.driver_id) left join udo_point as po on (po.id=rf.point_id) left join udo_location as lo on (lo.id=po._udo_Location_id) where rfo.orderNumber in (328882);
